import React from "react";
import { Question } from "../types";
import he from "he";

interface QuizProps {
  questions: Question[];
  selectedAnswers: string[];
  score: number | null;
  submitted: boolean;
  onAnswerSelect: (questionIndex: number, answer: string) => void;
  onSubmit: () => void;
  onReset: () => void;
}

const Quiz: React.FC<QuizProps> = ({
  questions,
  selectedAnswers,
  score,
  submitted,
  onAnswerSelect,
  onSubmit,
  onReset,
}) => {
  const getButtonStyle = (questionIndex: number, answer: string) => {
    if (!submitted) {
      return selectedAnswers[questionIndex] === answer
        ? "bg-green-500 text-white"
        : "bg-white text-green-500 border-2 border-green-500";
    }

    const currentQuestion = questions[questionIndex];
    if (answer === currentQuestion.correct_answer) {
      return "bg-green-500 text-white";
    }

    if (
      selectedAnswers[questionIndex] === answer &&
      answer !== currentQuestion.correct_answer
    ) {
      return "bg-red-500 text-white";
    }

    return "bg-white text-green-500 border-2 border-green-500";
  };

  const decodeHtml = (text: string | undefined): string => {
    if (!text) return "";
    try {
      return he.decode(text);
    } catch (error) {
      console.error("Error decoding HTML:", error);
      return text;
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      {questions.map((question, questionIndex) => {
        // Always include all possible answers and shuffle them
        const allAnswers = [
          ...question.incorrect_answers,
          question.correct_answer,
        ].sort(() => Math.random() - 0.5);

        return (
          <div
            key={questionIndex}
            className="mb-8 p-4 bg-white rounded-lg shadow"
          >
            <h2 className="text-xl font-bold mb-4">
              Question {questionIndex + 1} of {questions.length}
            </h2>
            <p className="text-lg mb-4">{decodeHtml(question.question)}</p>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
              {allAnswers.map((answer, answerIndex) => (
                <button
                  key={answerIndex}
                  className={`p-3 rounded-lg transition-colors ${getButtonStyle(
                    questionIndex,
                    answer
                  )}`}
                  onClick={() =>
                    !submitted && onAnswerSelect(questionIndex, answer)
                  }
                  disabled={submitted}
                >
                  {decodeHtml(answer)}
                </button>
              ))}
            </div>
          </div>
        );
      })}

      {submitted ? (
        <div className="text-center mb-8">
          <p className="text-xl font-bold mb-4">
            You scored {score} out of {questions.length}
          </p>
          <button
            onClick={onReset}
            className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-700"
          >
            Create a new quiz
          </button>
        </div>
      ) : (
        <div className="text-center">
          <button
            onClick={onSubmit}
            className="bg-blue-500 text-white font-bold py-2 px-4 rounded hover:bg-blue-700"
          >
            Submit Quiz
          </button>
        </div>
      )}
    </div>
  );
};

export default Quiz;
